mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT '자유' as tblName, f.free_num,
REPLACE(REPLACE(f.free_title,'<','&lt'),'>','&gt;') free_title,
f.free_hit, f.free_fixed, f.free_filename, f.free_regdate, (SELECT COUNT(*) FROM free_reply r WHERE r.free_num=f.free_num) f_replyCnt, (SELECT COUNT(*) FROM free_fav w WHERE w.free_num=f.free_num) f_favCnt, m.mem_num, m.mem_nick, d.mem_photo_name FROM free_board f JOIN member m ON m.mem_num=f.mem_num JOIN member_detail d ON d.mem_num=f.mem_num
ORDER BY f.free_regdate DESC)a)
WHERE rnum>=#{start} AND rnum<=#{end}
ORDER BY f.free_hit DESC)a)
WHERE rnum>=#{start} AND rnum<=#{end}
ORDER BY f_favCnt DESC, f.free_hit DESC)a)
WHERE rnum>=#{start} AND rnum<=#{end}
ORDER BY f_replyCnt DESC, f.free_hit DESC)a)
WHERE rnum>=#{start} AND rnum<=#{end}

mapper.update:
UPDATE free_board SET
free_uploadfile=#{free_uploadfile}, free_filename=#{free_filename},
free_title=#{free_title}, free_content=#{free_content}, free_ip=#{free_ip}, free_modifydate=SYSDATE WHERE free_num=#{free_num}
